mapper.select:
SELECT * FROM kcgbd.t_user
WHERE 1=1
and email like '%@team4.com'

	and user_id = #{user_id}


	and name = #{name}

ORDER BY jikgub_cd asc

SELECT * FROM kcgbd.t_user WHERE status_cd = #{status_cd}
AND email like '%@team4.com'
ORDER BY jikgub_cd asc

mapper.update:
UPDATE kcgbd.t_user
SET user_pw = #{user_pw}
WHERE user_id = #{user_id}

UPDATE kcgbd.t_user
SET 
del_yn = #{del_yn},
iam_yn = #{iam_yn},
status_cd = #{status_cd}
WHERE user_id = #{user_id}
