mapper.select:
SELECT * FROM kcgbd.t_user
WHERE 1=1
and email like '%@team4.com'

   and user_id = #{user_id}


   and name = #{name}

ORDER BY jikgub_cd asc

SELECT * FROM kcgbd.t_user WHERE 1=1
and status_cd = #{status_cd}
and name LIKE CONCAT('%', #{name}, '%') AND email like '%@team4.com' ORDER BY jikgub_cd asc

SELECT * FROM kcgbd.t_user WHERE status_cd = #{status_cd}
AND email like '%@team4.com'
ORDER BY jikgub_cd asc

mapper.update:
UPDATE kcgbd.t_user
SET
user_pw = #{user_pw}
WHERE user_id = #{user_id}
